mapper.select:
    select * from
    (select
    row_number() over(order by b.bno desc) as rw, b.*
	from board_view b)
	where ceil(rw / #{pageContentEa}) = #{pageNo}

select * from (
  select row_number() over(order by bno desc) as rw,
         bno, titles, usersId, bdate, bview, blike
  from board_view
  where titles like #{query}
)
where ceil(rw / #{pageContentEa}) = #{pageNo}

mapper.update:
update board
set titles = #{titles}, bpost = #{bpost}
where bno = #{bno}
